import React, { useState, useEffect } from "react";
import axios from "axios";

const Dashboard = ({ onBannerUpdate }) => {
  const [description, setDescription] = useState(""); // Default to an empty string
  const [timer, setTimer] = useState(60); // Default to 60 seconds
  const [link, setLink] = useState(""); // Default to an empty string
  const [isVisible, setIsVisible] = useState(true); // Default to true

  useEffect(() => {
    // Fetch current banner settings
    axios
      .get("http://localhost:5000/api/banner")
      .then((response) => {
        const { description, timer, link, isVisible } = response.data;
        setDescription(description || ""); // Fallback to an empty string if null or undefined
        setTimer(timer || 60); // Fallback to 60 seconds if null or undefined
        setLink(link || ""); // Fallback to an empty string if null or undefined
        setIsVisible(isVisible); // Set the boolean value directly
      })
      .catch((error) => console.error("Error fetching banner data:", error));
  }, []);

  const handleSubmit = () => {
    const updatedBanner = { description, timer, link, isVisible };
    axios
      .post("http://localhost:5000/api/banner", updatedBanner)
      .then((response) => {
        onBannerUpdate(updatedBanner);
        alert("Banner updated successfully");
      })
      .catch((error) => console.error("Error updating banner:", error));
  };
  console.log("Description:", description);
  console.log("Link:", link);
  console.log("Timer:", timer);
  console.log("Is Visible:", isVisible);

  return (
    <div className="dashboard">
      <h2>Banner Dashboard</h2>
      <div>
        <label>Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
      </div>
      <div>
        <label>Timer (seconds):</label>
        <input
          type="number"
          value={timer}
          onChange={(e) => setTimer(Number(e.target.value))}
        />
      </div>
      <div>
        <label>Link:</label>
        <input
          type="text"
          value={link}
          onChange={(e) => setLink(e.target.value)}
        />
      </div>
      <div>
        <label>Banner Visibility:</label>
        <input
          type="checkbox"
          checked={isVisible}
          onChange={(e) => setIsVisible(e.target.checked)}
        />
      </div>
      <button onClick={handleSubmit}>Update Banner</button>
    </div>
  );
};

export default Dashboard;
