import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import Dashboard from "./Components/Dashboard";
import Banner from "./Components/Banner";

const App = () => {
  const [bannerData, setBannerData] = useState({
    description: "",
    timer: 60,
    link: "",
    isVisible: true,
  });

  const handleBannerUpdate = (updatedBanner) => {
    setBannerData(updatedBanner);
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/" element={<Banner {...bannerData} />} />
          <Route
            path="/dashboard"
            element={<Dashboard onBannerUpdate={handleBannerUpdate} />}
          />
        </Routes>
      </div>
    </Router>
  );
};

// Home Component
const Home = () => (
  <div>
    <h1>Welcome to the Banner Management System</h1>
    <p>Use the navigation links to go to the Dashboard or view the Banner.</p>
  </div>
);

export default App;
